	public static function getOptionArray() {
		$options = array ();
		foreach ( self::$_transitions as $trans ) {
			$options[$trans['value']] = $trans['label'];
		}
		return $options;
	}
	
	public static function toOptionArray() {
		$options[] = array (
			'value' => '',
			'label' => Mage::helper('slider')->__ ('--Please Select--')
		);
		foreach ( self::$_transitions as $trans ) {
			$options[] = $trans;
		}
		return $options;
	}
